var series =["Setpoint in Â°C"];
var labels = [""];
var data = [];

//get start time
var date = new Date();
var lastTime = date.getTime();

//get protocol
var protocol = flow.get('protocol');
var lastTemp = flow.get("temp");
var count = flow.get("count");
var heatingRate = 0.015789474; //deg/s
var lag = 120*1000;

//construct time series
for ( count; count < protocol.length; count++){

    data.push({"x": lastTime, "y": lastTemp});

    //calculate temperature difference
    var tempDiff = protocol[count].Setpoint - lastTemp;

    //check if heating, if too far add lag
    var offset = Math.round((tempDiff / heatingRate) * 1000);

    if (tempDiff >= 20) {
        data.push({"x": lastTime+lag, "y": lastTemp});
    } else if (tempDiff < 0) {        
        offset = 0;
    };

    var thisDuration = protocol[count].Duration*60*1000;
    data.push({"x": lastTime + offset, "y": protocol[count].Setpoint}); //heating up
    data.push({"x": lastTime + thisDuration + offset, "y": protocol[count].Setpoint}); //stay at Setpoint

    lastTime += thisDuration + offset;
    lastTemp = protocol[count].Setpoint;
}

data = [data];

msg.topic = "";
msg.payload = [{series, data, labels}];
return msg;
